mapper.insert:
insert into doc.medication (id, nomenclature, manufacturer, dose_form, dose_val, mu)
values (#{medication.id}, #{medication.nomenclature}, #{medication.manufacturer},
        #{medication.doseForm}, #{medication.doseVal}, #{medication.measurementUnits});

mapper.select:
select id, nomenclature, manufacturer, dose_form, dose_val, mu
from doc.medication
where id = #{id};

select id, nomenclature, manufacturer, dose_form, dose_val, mu
from doc.medication
limit #{limit} offset #{offset};
