mapper.insert:
insert into doc.medorder (id, supplier, form_date, delivery_date, status)
values (#{medOrder.id}, #{medOrder.supplier}, #{medOrder.formDate}, #{medOrder.deliveryDate},
        #{medOrder.status.code});

mapper.select:
select id, supplier, form_date, delivery_date, status
from doc.medorder
where id = #{id};

select id, supplier, form_date, delivery_date, status
from doc.medorder
limit #{limit} offset #{offset};
