mapper.select:
select id, full_name, phone_number, adverse_reactions, date_of_birth, sex
from doc.patient
limit #{limit} offset #{offset}

select code
from doc.patient_icd
where patient_id = #{patientId}

mapper.delete:
delete
from doc.patient_icd
where patient_id = #{patientId}
  and code = #{icdCode}
